import React, { Component } from 'react';
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";

export default class Addproduct extends Component {
    state={
      startDate: new Date(),
      selectedFile: null,
    };

    //setting state for any change
    handleChange = date => {
      console.log('date selected: ', date)
      this.setState({
        startDate: date
      });
    };

    // On file upload (click the upload button) 
    onFileUpload = () => { 

      // Create an object of formData 
      const formData = new FormData(); 

      // Update the formData object 
      formData.append( 
        "myFile", 
        this.state.selectedFile, 
        this.state.selectedFile.name 
      );
      // Details of the uploaded file 
      console.log(this.state.selectedFile);  
      const fileData = () => { 

        if (this.state.selectedFile) { 

          return ( 
            <div> 
              <h2>File Details:</h2> 
              <p>File Name: {this.state.selectedFile.name}</p> 
              <p>File Type: {this.state.selectedFile.type}</p> 
              <p> 
                Last Modified:{" "} 
                {this.state.selectedFile.lastModifiedDate.toDateString()} 
              </p> 
            </div> 
          ); 
        } else { 
          return ( 
            <div> 
              <br /> 
              <h4>Choose before Pressing the Upload button</h4> 
            </div> 
          ); 
        } 
      }; 
    }  


    handlechange = (event) => {
        this.setState({ [event.target.name]: event.target.value });
      };

    render() {
        return (
            <div>
                <h1>Add product descrption</h1>
                <form onSubmit={this.handlesubmit}>
                <label>
          Product Descrption:
          <br />
          <textarea value={this.state.value} onChange={this.handleChange} />
        </label>
          <br />
          <label>
            Crop type:
            <br />
            <select value={this.state.value} onChange={this.handleChange}>
              <option value="tamato">Tamato</option>
              <option value="chilli">Chilli</option>
              <option value="rice">Rice</option>
              <option value="wheat">Wheat</option>
              <option value="Mango">Mango</option>
              <option value="sapota">Sapota</option>
              <option value="Mosambi">Mosambi</option>
              <option value="Toor dal">Tor dal</option>
            </select>
          </label>
          <br />
          <label> Weight</label>
          <br />
          <input
            type="number"
            name="number"
            value={this.state.name}
            placeholder= "weight in kgs/Tons"
            onChange={this.handlechange}
          />{" "}
          <br />
          <label> Price </label>
          <br />
          <input
            type="number"
            name="number"
            value={this.state.name}
            placeholder= "Price in Rs"
            onChange={this.handlechange}
          />{" "}
          <br />
          <br />
          <label>
             Location:
             <br />
            <select value={this.state.value} onChange={this.handleChange}>
              <option value="hyderabad">Hyderabad</option>
              <option value="adilabad">Adilabad</option>
              <option value="bhadradri">Bhadradri</option>
              <option value="jagtial">Jagtial</option>
              <option value="kamareddy">Kamareddy</option>
              <option value="karimnagar">Karimnagar</option>
              <option value="khammam">Khammam</option>
              <option value="medak">mMedak</option>
              <option value="nirmal">Nirmal</option>
              <option value="warangal">Warangal</option>

            </select>
          </label>
          <br />
          <br />
          <input type="file" onChange={this.onFileChange} /> 
                <button onClick={this.onFileUpload}> 
                  Upload! 
                </button>
                <br />
                <br />
                <label>Bid End Date:</label>
                <br />
                <DatePicker
          selected={this.state.startDate}
          onChange={this.handleChange}
        />
        <br />
        <br />
          <input type="submit" value="Submit" />
        </form>
            </div>
        );
    }
}
