import React, { Component } from 'react';
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";

export default class Addproduct extends Component {
    state={
      name: "",
      croptype: "",
      weight: "",
      price: "",
      selectlocation: "",
      files: null,
      startDate: new Date(),
    };

    handleChangeName = (e) => {
      this.setState({
        name:e.target.value
      })
    }
    handleChangeCroptype = (event) => {
      this.setState({
       croptype:event.target.value
      })
    }
    handleChangeWeight = (event) => {
      this.setState({
        weight:event.target.value
      })
    }
    handleChangePrice = (event) => {
      this.setState({
        price:event.target.value
      })
    }
    handleChangeSelectlocation = (event) => {
      this.setState({
       selectlocation:event.target.value
      })
    }
    // choose file event
    handleChangefiles = (event) => {
      console.log(event.target.files[0]);
      this.setState({
        files: URL.createObjectURL(event.target.files[0])
      })
    }

    handleupload = (event) => {
      console.log('fileuplaoded:');
    }
    handlechange = (event) => {
        this.setState({ [event.target.name]: event.target.value });
      };

       //setting state for any change
    handleChangeDate = date => {
      console.log('date selected: ', date)
      this.setState({
        startDate: date
      });
    };

      handlesubmit = (event) => {
        alert( `My description is ${this.state.description}.
                My crop type is ${this.state.croptype}.
                My weight is ${this.state.weight}.
                My price  is ${this.state.price}.
                My location type is ${this.state.selectlocation}.
                My selectfile is ${this.state.selectedFile}.
                My Bidend date is ${this.state.startDate}.
              `);
        //console.log(JSON.stringify(this.state));
        event.preventDefault();
      };

    render()  {
      console.log(this.state.selectfile);
        return (
            <div>
                <h1>Add product descrption</h1>
                <form onSubmit={this.handlesubmit}>
                <label>
              Name:
              <br />
              <input type="text" style={{
                borderRadius: "5px",
                width: "400px",
                border: "1px solid #CED4DA",
              }}
              onChange={this.handleChangeName} />
            </label>
              <br />
              <label>
              Title:
              <br />
              <input type="text" style={{
                borderRadius: "5px",
                width: "400px",
                border: "1px solid #CED4DA",
              }}
              onChange={this.handleChangeName} />
            </label>
              <br />
              <div style={{ paddingRight: "297px" }} />
              <label  style={{
                color: "black",
                paddingRight: "50px",
              }}>
                Crop type:
                <br />
                <select value={this.state.value} onChange={this.handleChangeCroptype}
                style={{
                  backgroundColor: "#007BFF",
                  borderRadius: "5px",
                  width: "135px",
                  height: "30px",
                  color: "white",
                  paddingLeft: "10px",
                  paddingRight: "25px",
                  border: "1px solid #CED4DA",
                }}
                >
                  <option value="tamato">Tamato</option>
                  <option value="chilli">Chilli</option>
                  <option value="rice">Rice</option>
                  <option value="wheat">Wheat</option>
                  <option value="Mango">Mango</option>
                  <option value="sapota">Sapota</option>
                  <option value="Mosambi">Mosambi</option>
                  <option value="Toor dal">Tor dal</option>
                </select>
              </label>
              <br />
              <label> Weight: </label>
              <br />
              <input
                type="number"
                placeholder= "weight in kgs/Tons"
                onChange={this.handleChangeWeight}
                style={{
                  borderRadius: "5px",
                  width: "400px",
                  border: "1px solid #CED4DA",
                }}
              />{" "}
              <br />
              <label style={{
                color: "black",
                paddingRight: "50px",
              }}> Price: </label>
              <br />
              <input
                type="number"
                placeholder= "Price in Rs"
                onChange={this.handleChangePrice}
                style={{
                  borderRadius: "5px",
                  width: "400px",
                  border: "1px solid #CED4DA",
                }}
              />{" "}
              <br />
              <br />
              <div style={{ paddingRight: "297px" }} />
              <label style={{
                color: "black",
                paddingRight: "50px",
              }}>
                Location:
                <br />
                <select value={this.state.value} onChange={this.handleChangeSelectlocation}
                style={{
                  backgroundColor: "#007BFF",
                  borderRadius: "5px",
                  width: "135px",
                  height: "30px",
                  color: "white",
                  paddingLeft: "10px",
                  paddingRight: "25px",
                  border: "1px solid #CED4DA",
                }}>
                  <option value="hyderabad">Hyderabad</option>
                  <option value="adilabad">Adilabad</option>
                  <option value="bhadradri">Bhadradri</option>
                  <option value="jagtial">Jagtial</option>
                  <option value="kamareddy">Kamareddy</option>
                  <option value="karimnagar">Karimnagar</option>
                  <option value="khammam">Khammam</option>
                  <option value="medak">mMedak</option>
                  <option value="nirmal">Nirmal</option>
                  <option value="warangal">Warangal</option>

                </select>
              </label>
              <br />
              <br />
              <label>Upload images:</label>
              <br />
              <input type='file' onChange={this.handleChangefiles} 
              style={{
                color: "black",
                paddingRight: "50px",
              }}/>
              <img src={this.state.files} />
              <br />
              <button type='button' onClick={this.handleupload} style={{
                backgroundColor: "#007BFF",
                borderRadius: "5px",
                width: "102px",
                height: "30px",
                color: "white",
                paddingLeft: "10px",
                paddingRight: "25px",
                border: "1px solid #CED4DA",
              }}>Upload</button>
                    <br />
                    <br />
                    <label>Bid End Date:</label>
                    <br />
                    <DatePicker
              selected={this.state.startDate}
              onChange={this.handleChangeDate}
            />
            <br />
            <br />
              <input type="submit" value="Submit"
              style={{
                color: "white",
                backgroundColor: "#007BFF",
                borderRadius: "5px",
                width: "510px",
