function App(props) {
  const { isAuthenticated, isVerifying } = props;
  return (<Router>
    <div className="App">
      <nav className="navbar navbar-expand-lg navbar-light fixed-top">
        <div className="container">
          <Link className="navbar-brand" to={"/Farmerhome"}>Farmerhome</Link>
          <Link className="navbar-brand" to={"/Addproduct"}>Addproduct</Link>
          <div className="collapse navbar-collapse" id="navbarTogglerDemo02">
            <ul className="navbar-nav ml-auto">
              <li className="nav-item">
                <Link className="nav-link" to={"/sign-in"}>Login</Link>
              </li>
              <li className="nav-item">
                <Link className="nav-link" to={"/sign-up"}>Sign up</Link>
              </li>
            </ul>
          </div>
        </div>
      </nav>

      <div className="auth-wrapper">
        <div className="auth-inner">
          <Switch>
          <ProtectedRoute
        exact
        path="/"
        component={Home}
        isAuthenticated={isAuthenticated}
        isVerifying={isVerifying}
      />
          <Route path="/login" component={Login} />
                  <Route exact path="/Register" component={Register} />
                  <Route exact path="/Farmerhome" component={Farmerhome} />
                  <Route exact path="/Addproduct" component={Addproduct} />
                  <Route exact path="/Viewdetails/:id" component={Viewdetails} />
                  <Route exact path="/Yourbids" component={Yourbids} />
                  <Route exact path="/Bidprice" component={Bidprice} />
          </Switch>
        </div>
      </div>
    </div></Router>
  );
}
