import React, { Component } from "react";
import { Link, Redirect } from "react-router-dom";
import { connect } from "react-redux";
import { loginUser } from "../actions";

class Login extends Component {
    state = {
        mobilenum: '',
        otp: '',
        isLogged: false,
      };

    handleChangeNum = (event) => {
      this.setState({
        mobilenum: event.target.value,
      });
    };
    handleChangeOtp = (event) => {
      this.setState({
        otp: event.target.value,
      });
    };
    handlePasswordChange = ({ event }) => {
        this.setState({ password: event.target.value });
      };

    handleSubmit = () => {
        const { dispatch } = this.props;
        const { email, password } = this.state;

        dispatch(loginUser(email, password));
    }
    render() {
        const { classes, loginError, isAuthenticated } = this.props;
        if (isAuthenticated){
            return <Redirect to="/Farmerhome" />
        }
        return (
            <div className='loginpage'>
            <form>
            <h3>Farmer App</h3>
            <div className="form-group">
                <label>Mobile number</label>
                <input type="text" className="form-control" placeholder="Enter number" onChange={this.handleChangeNum} />
            </div>

            <div className="form-group">
                <label>One Time Password</label>
                <input type="text" className="form-control" placeholder="Enter OTP" onChange={this.handleChangeOtp} />
            </div>

            <div className="form-group">
                <div className="custom-control custom-checkbox">
                    <input type="checkbox" className="custom-control-input" id="customCheck1" />
                    <label className="custom-control-label" htmlFor="customCheck1">Remember me</label>
                </div>
            </div>

            <button type="submit" className="btn btn-primary btn-block" onClick={this.handleSubmit}>Submit</button>
            <p className="forgot-password text-right">
                Forgot <a href="#">password?</a>
            </p>
            <p className="New register text-left">
            Register<Link to={"/Register"}>Here</Link>
        </p>
        </form>
        </div>
    );
}
}

function mapStateToProps(state) {
    return {
      isLoggingIn: state.auth.isLoggingIn,
      loginError: state.auth.loginError,
      isAuthenticated: state.auth.isAuthenticated
    };
  }
  export default withStyles(styles) (connect(mapStateToProps)(Login));



