import React, { Component } from 'react'
import { connect } from "react-redux";
import store from "../configureStore";
import { Link } from 'react-router-dom';
import { Button } from 'react-bootstrap';
import Navigation from './Navgation';


class Viewdetails extends Component {
    storecp = store.getState()
    productId 
    product = []
    constructor(props){
        super(props)
        this.productId = this.props.match.params.id
        console.log(this.props.match.params.id)

        this.product = this.storecp.auth.products.filter( (pd)=>{
            if(pd.id === this.productId){
                return pd
            }
        })
        if (this.product.length>0) {
            this.product = this.product[0]    
        }
    }

    productToRender(){
        if (this.product) {
            return <div>
                <Navigation></Navigation>
                <h3>Viewdetails</h3>
                <div>posted by:{this.product.name}</div>
                <div>croptype:{this.product.croptype}</div>
                <div>weight in kgs:{this.product.weight}</div>
                <div>price in rs:{this.product.price}</div>
                <div>location:{this.product.selectlocation}</div>
                <div>image:{this.product.files}</div>
                <div>Date:{this.product.startDate.toDateString()}</div>
                <Button variant="primary" className='card_btn'>
                <Link style={{color:'white'}} to="/Bidprice"> Submit bid</Link>
                </Button>
            </div>
        } else {
            return <div>
                Product not found
            </div>
        }
    }

    render() {



        return (
            <div className="view">

                <p>
                    Back<Link to={"/Farmerhome"}>Here</Link>
                </p>
            </div>
        )

    }

}

function mapStateToProps(state) {
return {

};
}

export default connect(mapStateToProps)(Viewdetails);
